public class JsonToObject {
  public static String PATH = getPath()+"/output";
  public static String INPUT_PATH = getPath()+"/input.txt";
  
  

  
  public static void main(String[] args) throws Exception {
	  	  	  	  
    int startIndex = 1;
           
    String path = "";
   // String jsonString = "[ {\"id\": \"2f99fd40-63ac-4898-bd2b-cba910385ac4\", \"messageId_s\": \"SSM36320170521185236001\", \"messageType_s\": \"SS_PT2PT\", \"messagePartner_s\": \"SAMSUNGSDSGSCLPORT\", \"messageContent_bin\": \"\", \"persisted_dt\": \"2017-05-21T18:52:37.032Z\"},{ \"id\": \"ba0be5f5-ba7a-4fff-ad70-50686ff3ffd3\", \"messageId_s\": \"SSM36320170521185236001\", \"messageType_s\": \"SS_PT2PT\", \"messagePartner_s\": \"SAMSUNGSDSGSCLPORT\", \"messageContent_bin\": \"\", \"persisted_dt\": \"2017-05-21T18:52:36.759Z\"},{ \"id\": \"aa8850f1-89f2-4571-a6b9-c04ef2cd0f4d\", \"messageId_s\": \"SSM36320170521185236001\", \"messageType_s\": \"SS_PT2PT\", \"messagePartner_s\": \"SAMSUNGSDSGSCLPORT\", \"messageContent_bin\": \"\", \"persisted_dt\": \"2017-05-21T18:52:36.464Z\"},{ \"id\": \"d1e9f327-8c31-425c-9ed9-2c515b615b2c\", \"messageId_s\": \"SSM36320170521185235001\", \"messageType_s\": \"SS_PT2PT\", \"messagePartner_s\": \"SAMSUNGSDSGSCLPORT\", \"messageContent_bin\": \"\", \"persisted_dt\": \"2017-05-21T18:52:36.311Z\"},{ \"id\": \"66226076-824d-46ff-85fc-d00825f226cf\", \"messageId_s\": \"SSM36320170521185235001\", \"messageType_s\": \"SS_PT2PT\", \"messagePartner_s\": \"SAMSUNGSDSGSCLPORT\", \"messageContent_bin\": \"\", \"persisted_dt\": \"2017-05-21T18:52:35.911Z\"}]";
    File inputFile = new File(INPUT_PATH);
    String jsonString = NewFileUtil.readTxtFile(inputFile);
    jsonString = "["+jsonString+"]";
    StringEscapeUtils.escapeCsv(jsonString);
    //System.out.println(jsonString);
    ArrayList<TestObject2> tests = new JacksonBuilder().getObjects(jsonString);
    Map<String, Integer> id = new HashMap<String, Integer>();
    for (TestObject2 test : tests) {
      if (id.containsKey(test.getMessageId_s())) {
        id.put(test.getMessageId_s(), id.get(test.getMessageId_s()) + 1);
        path = PATH + "\\" + test.getMessageId_s() + "_" + id.get(test.getMessageId_s());
        System.out.println(test.getMessageId_s());
      } else {
        path = PATH + "\\" + test.getMessageId_s();
        id.put(test.getMessageId_s(), startIndex);
        System.out.println(test.getMessageId_s());
      }
      //System.out.println(test.getMessageContent_bin());
      String content = TestUnzip.unzip(test.getMessageContent_bin());
            
      NewFileUtil.contentToTxt(path, content);
             
    }
    System.out.println("Unzip successful!"); 
  }

  
  public static String getPath(){
	  String path="";
	  String p = JsonToObject.class.getResource("/").toString();
	  String p1 = p.substring(6);
	  path = StringUtils.substringBefore(p1, "/target/classes/");
	  //System.out.println(p1);
	  return path;
  }
  
}